import React, { useState } from "react";
import { Link } from "react-scroll";
import Theme from "./Theme";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faX } from "@fortawesome/free-solid-svg-icons";

const Navbar = () => {
  let Links = [
    { name: "ABOUT", to: "about", id: "about" },
    { name: "PROJECTS", to: "projects", id: "projects" },
    { name: "CONTACT", to: "contact", id: "contact" },
  ];
  let [open, setOpen] = useState(false);

  return (
    <div className="w-full md:absolute fixed top-0 left-0">
      <div className="md:flex items-center justify-between md:py-10 py-5 md:p-10vw px-7">
        <div className="text-2xl dark:text-white text-black font-merriweather font-bold">
          BARA KHARSEH
        </div>

        <div
          onClick={() => setOpen(!open)}
          className="dark:text-white text-black text-3xl absolute right-8 top-4 cursor-pointer md:hidden"
        >
          <FontAwesomeIcon icon={open ? faX : faBars} />
        </div>

        <ul
          className={`dark:text-white text-black font-merriweather md:flex md:items-center absolute md:static md:z-auto z-[-1] w-full md:w-auto transition-all duration-300 ease-in ${
            open ? "top-20 opacity-100" : "left-[-100px]"
          } md:opacity-100`}
        >
          {Links.map((link) => (
            <li key={link.name} className="md:ml-8 text-xl md:my-0 my-7">
              <Link
                to={link.to}
                spy={true}
                smooth={true}
                duration={500}
                className="hover:text-gray-400 duration-300 scroll-smooth hover:cursor-pointer"
              >
                {link.name}
              </Link>
            </li>
          ))}
          <Theme />
        </ul>
      </div>
    </div>
  );
};

export default Navbar;
