import React from "react";
import { useForm, ValidationError } from "@formspree/react";

const Contact = () => {
  const [state, handleSubmit] = useForm("xjvzgleb");
  if (state.succeeded) {
    return <p>Thanks for the email!</p>;
  }

  return (
    <div>
      <h1 className="text-4xl dark:text-white text-black font-bold font-merriweather text-center py-10">
        Contact Me
      </h1>
      <form onSubmit={handleSubmit}>
        <div className="flex flex-col w-1/3 justify-center text-center">
          <label htmlFor="name" className="dark:text-white text-black">
            Name
          </label>
          <input id="name" type="name" name="name" />
          <label htmlFor="email" className="dark:text-white text-black">
            Email Address
          </label>
          <input id="email" type="email" name="email" />
          <ValidationError prefix="Email" field="email" errors={state.errors} />
          <textarea id="message" name="message" />
          <ValidationError
            prefix="Message"
            field="message"
            errors={state.errors}
          />
          <button type="submit" disabled={state.submitting}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default Contact;
