import React, { useDeferredValue, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMoon, faSunBright } from "@fortawesome/free-solid-svg-icons";

const Theme = () => {
  let [dark, setDark] = useState(true);
  return (
    <button
      onClick={() => setDark(!dark)}
      className="text-xl md:ml-8 hover:text-gray-500 duration-300"
    >
      {dark && <FontAwesomeIcon icon="fa-duotone fa-moon" />}
      {!dark && <FontAwesomeIcon icon="fa-solid fa-sun-bright" />}
    </button>
  );
};

export default Theme;
