import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFile } from "@fortawesome/free-solid-svg-icons";
import { faGithub, faLinkedin } from "@fortawesome/free-brands-svg-icons";

const Home = () => {
  const [helloWorld, setHelloWorld] = useState("Hello, World!");
  const [iAm, setIAm] = useState(" a software engineer.");

  setInterval(() => {
    if (helloWorld == "Hello, World!") {
      setHelloWorld("👋🏼, 🌍!");
    } else {
      setHelloWorld("Hello, World!");
    }
  }, 3000);

  setInterval(() => {
    if (iAm == " a software engineer.") {
      setIAm(" a second year U of T student.");
    } else if (iAm == " a second year U of T student.") {
      setIAm(" an athlete.");
    } else if (iAm == " an athlete.") {
      setIAm(" a reader.");
    } else {
      setIAm(" a software engineer.");
    }
  }, 3000);

  return (
    <div className="flex h-screen flex-col justify-center items-center">
      <h1 className="text-6xl dark:text-white text-black font-bold font-merriweather text-center md:mb-10 animate-fadeInOut">
        {helloWorld}
      </h1>
      <h1 className="text-6xl dark:text-white text-black font-bold font-merriweather text-center md:mb-10">
        My name is Bara Kharseh.
      </h1>
      <div className="flex text-6xl dark:text-white text-black font-bold font-merriweather text-center md:mb-10">
        <h1>I am</h1>
        <h1 className="animate-fadeInOut">
          {iAm}
        </h1>
      </div>
      <div class="flex space-x-20 text-2xl dark:text-white text-black font-merriweather">
        <a
          href="https://github.com/BaraKharseh"
          target="_blank"
          className="hover:text-gray-500 duration-300"
        >
          <FontAwesomeIcon icon={faGithub} /> GitHub
        </a>
        <a
          href="https://www.linkedin.com/in/bara-kharseh-a146b521a"
          target="_blank"
          className="hover:text-blue-500 duration-300"
        >
          <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
        </a>
        <a
          href="https://drive.google.com/file/d/19NweXjkQZWRDF__YmRn-sdNAVwrE8Nbd/view?usp=sharing"
          target="_blank"
          className="hover:text-rose-500 duration-300"
        >
          <FontAwesomeIcon icon={faFile} /> Resume
        </a>
      </div>
    </div>
  );
};

export default Home;
