import React, { useState } from "react";
import Theme from "./Theme";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faX } from "@fortawesome/free-solid-svg-icons";
import useDarkMode from "../hook/useDarkMode";

const Navbar = () => {
  let Links = [
    { name: "ABOUT", link: "/" },
    { name: "PROJECTS", link: "/" },
    { name: "CONTACT", link: "/" },
  ];
  let [open, setOpen] = useState(false);
  const [colorTheme, setTheme] = useDarkMode();
  return (
    <div className="w-full absolute top-0 left-0">
      <div className="md:flex items-center justify-between py- md:px-40 px-7">
        <div className="text-2xl dark:text-white text-black font-merriweather font-bold">
          <a href="/" className="hover:text-gray-400 duration-300">
            BARA KHARSEH
          </a>
        </div>

        <div
          onClick={() => setOpen(!open)}
          className="dark:text-white text-black text-3xl absolute right-8 top-9 cursor-pointer md:hidden"
        >
          <FontAwesomeIcon icon={open ? faX : faBars} />
        </div>

        <ul
          className={`dark:text-white text-black font-merriweather md:flex md:items-center md:pb-0 pb-12 absolute md:static md:z-auto z-[-1] left-0 w-full md:w-auto md:pl-0 pl-9 transition-all duration-300 ease-in ${
            open ? "top-20 opacity-100" : "top-[-300px]"
          } md:opacity-100`}
        >
          {Links.map((link) => (
            <li key={link.name} className="md:ml-8 text-2xl md:my-0 my-7">
              <a href={link.link} className="hover:text-gray-400 duration-300">
                {link.name}
              </a>
            </li>
          ))}
          <div>
            <Theme />
          </div>
        </ul>
      </div>
    </div>
  );
};

export default Navbar;
