const Footer = () => {
  const [colorTheme, setTheme] = useDarkMode();
  const footer = "";
  if (colorTheme == "light") {
    footer = "Made with ü§ç by Bara Kharseh";
  } else {
    footer = "Made with üñ§ by Bara Kharseh";
  }

  return (
    <div>
      <h1 className="text-lg font-merriweather text-center text-black dark:text-white pb-5">
        {footer}
      </h1>
    </div>
  );
};

export default Footer;
